function getTimers(){let n=0,i=[],t=document.querySelector(`#timer${n}`);while(t!=null)i.push(t),n++,t=document.querySelector(`#timer${n}`);return i}function countDown(){let n=getTimers();if(n.length!=0)for(let t=0;t<n.length;t++){let c=document.querySelector(`#timer${t}`).getAttribute("data-enddate"),l=new Date(c).getTime(),a=(new Date).getTime(),n=l-a,s=1e3,e=s*60,o=e*60,h=o*24,i=Math.floor(n/h),r=Math.floor(n%h/o),u=Math.floor(n%o/e),f=Math.floor(n%e/s);i<"10"&&(i="0"+i);r<"10"&&(r="0"+r);u<"10"&&(u="0"+u);f<"10"&&(f="0"+f);document.querySelector(`#days${t}`).textContent=i;document.querySelector(`#hours${t}`).textContent=r;document.querySelector(`#minutes${t}`).textContent=u;document.querySelector(`#seconds${t}`).textContent=f}}setInterval(countDown,1e3);